CREATE PROCEDURE spDelete
(
	 @Id INT
	,@Tabela VARCHAR(MAX)
)
AS

BEGIN
INSERT INTO Avalicoes(Cliente_Id,Livro_Id,Nota,Resenha) VALUES (@Cliente_Id,@Livro_Id,@Nota,@Resenha);
END

BEGIN
INSERT INTO Capa_Livro(Livro_Id,Capa_Livro) VALUES (@Livro_Id,@Capa_Livro);
END

BEGIN
INSERT INTO Clientes(Rfid_Id,Usuario_Id,Genero_Id,Sexo_Id,Nome,Cpf,Email,Telefone,Endereco,Data_nascimento,Saldo) VALUES (@Rfid_Id,@Usuario_Id,@Genero_Id,@Sexo_Id,@Nome,@Cpf,@Email,@Telefone,@Endereco,@Data_nascimento,@Saldo);
END

BEGIN
INSERT INTO Generos (Descricao) VALUES (@Descricao);
END

CREATE PROCEDURE spInsert_Generos
