CREATE PROCEDURE spUpdate_Sexos
(
	 @Id		INT
	,@Descricao	VARCHAR(MAX)
)
AS

BEGIN
-- Defina o novo salt DECLARE @salt UNIQUEIDENTIFIER = NEWID();
-- Combine a senha e o salt DECLARE @salt_password NVARCHAR(200) = @password + CAST(@salt AS NVARCHAR(36));
-- Crie o hash SHA-256 da senha + salt DECLARE @hash_password VARBINARY(64) = HASHBYTES('SHA2_256', @salt_password);
-- Inserir os dados UPDATE Usuarios SET name_user = @name_user ,hash_password = @hash_password ,salt_password = @salt_password ,user_type = @user_type WHERE Id = @id
END
